const Cards = ({ movie }) => {
  return (
    <div className="cards">
      <div className="img-container">
        <img
          src={
            movie.poster_path
              ? "https://image.tmdb.org/t/p/original" + movie.poster_path
              : "./img/poster.jpg"
          }
          alt={movie.poster_path}
          className="img-card"
        />
      </div>
      <div className="info-hover">
        <h1>{movie.title}</h1>
      </div>
    </div>
  );
};

export default Cards;
