import React, { useState } from "react";
import NavBar from "../components/NavBar";
import axios from "axios";

const Acceuil = () => {
  const [inputValue, setInputValue] = useState("");
  let axio = "";

  const handleInputChange = (newInputValue) => {
    setInputValue(newInputValue);
  };

  // _________________________________

  if (inputValue === "") {
    axio =
      "https://api.themoviedb.org/3/search/multi?api_key=864b6602f4018630491e67fa714381e6&query=av&page=1&language=fr-FR";
  } else {
    axio =
      "https://api.themoviedb.org/3/search/multi?api_key=864b6602f4018630491e67fa714381e6&query=" +
      inputValue +
      "&page=1&language=fr-FR";
  }

  useEffect(() => {
    axios.get(axio).then((res) => setData(res.data.results));
  }, [inputValue]);

  return (
    <div className="acceuil">
      <NavBar onInputChange={handleInputChange} />
      <div className="img-background"></div>
    </div>
  );
};

export default Acceuil;
