import axios from "axios";
import React, { useEffect, useState } from "react";
import Cards from "../components/Cards";
import Header from "../components/Header";

const Films = () => {
  const [data, setData] = useState([]);
  const [inputValue, setInputValue] = useState("");
  let [sortGoodBad, setSortGoodBad] = useState("");
  let axio = "";
  const [count, setCount] = useState(1);

  const handleInputChange = (newInputValue) => {
    setInputValue(newInputValue);
  };

  if (inputValue === "") {
    axio =
      "https://api.themoviedb.org/3/movie/top_rated?api_key=864b6602f4018630491e67fa714381e6&query=harry&page=" +
      count +
      "&language=fr-FR";
  } else {
    axio =
      "https://api.themoviedb.org/3/search/movie?api_key=864b6602f4018630491e67fa714381e6&query=" +
      inputValue +
      "&page=" +
      count +
      "&language=fr-FR";
  }

  useEffect(() => {
    axios.get(axio).then((res) => setData(res.data.results));
  }, [inputValue, count]);

  const search = (e) => {
    setInputValue(e.target.value);
  };

  function increment() {
    setCount(function (prevCount) {
      return (prevCount += 1);
    });
    console.log(count);
  }

  function decrement() {
    setCount(function (prevCount) {
      if (prevCount > 1) {
        return (prevCount -= 1);
      } else {
        return (prevCount = 1);
      }
    });
    console.log(count);
  }

  return (
    <div className="container">
      <NavBar onInputChange={handleInputChange} />
      <div className="search">
        <input
          onChange={(e) => search(e)}
          className="text"
          type="text"
          placeholder="Trouver un film..."
        />
        <input className="submit" type="submit" value={"Rechercher"} />
        <div className="btn-sort-container">
          <div
            onClick={() => setSortGoodBad("goodToBad")}
            className="btn-sort"
            id="goodToBad"
          >
            Top <span>⬆</span>
          </div>
          <div
            onClick={() => setSortGoodBad("badToGood")}
            className="btn-sort"
            id="badToGood"
          >
            Flop <span>⬇</span>
          </div>
        </div>
      </div>
      <ul className="catalogue">
        {data
          .sort((a, b) => {
            if (sortGoodBad === "goodToBad") {
              return b.vote_average - a.vote_average;
            } else if (sortGoodBad === "badToGood") {
              return a.vote_average - b.vote_average;
            }
          })
          .map((movie) => (
            <Cards key={movie.id} movie={movie} />
          ))}
      </ul>
      <div className="arrow-pages">
        <img
          onClick={() => {
            decrement();
          }}
          className="previous-page"
          src="./img/arrow.png"
          alt="Page pécédente"
        />
        <p className="page-number">{count}</p>
        <img
          onClick={() => {
            increment();
          }}
          className="next-page"
          src="./img/arrow.png"
          alt="Page suivante"
        />
      </div>
    </div>
  );
};

export default Films;
