import React, { useEffect, useState } from "react";

import NavBar from "../components/NavBar";
import axios from "axios";
import Cards from "../components/Cards";

const Acceuil = () => {
  const [inputValue, setInputValue] = useState("");
  const [count, setCount] = useState(1);
  let axio = "";
  const [data, setData] = useState([]);

  const handleInputChange = (newInputValue) => {
    setInputValue(newInputValue);
  };

  // _________________________________

  if (inputValue === "") {
    axio =
      "https://api.themoviedb.org/3/search/multi?api_key=864b6602f4018630491e67fa714381e6&query=av&page=" +
      count +
      "&language=fr-FR";
  } else {
    axio =
      "https://api.themoviedb.org/3/search/multi?api_key=864b6602f4018630491e67fa714381e6&query=" +
      inputValue +
      "&page=" +
      count +
      "&language=fr-FR";
  }
  useEffect(() => {
    axios.get(axio).then((res) => setData(res.data.results));
  }, [inputValue, count]);

  function increment() {
    setCount(function (prevCount) {
      return (prevCount += 1);
    });
  }

  function decrement() {
    setCount(function (prevCount) {
      if (prevCount > 1) {
        return (prevCount -= 1);
      } else {
        return (prevCount = 1);
      }
    });
  }

  return (
    <div className="acceuil">
      <NavBar onInputChange={handleInputChange} />
      <div className="img-background">
        <h1>AVATAR : LE DERNIER MAÃŽTRE DE L'AIR</h1>
      </div>
      <div className="card-container">
        <ul>
          {data.map((movie) => (
            <Cards key={movie.id} movie={movie} />
          ))}
        </ul>
      </div>
      <div className="arrow-pages">
        <a href="#nav-bar">
          <i
            class="fa-solid fa-chevron-left"
            onClick={() => {
              decrement();
            }}
          ></i>
        </a>

        <p className="page-number">{count}</p>
        <a href="#nav-bar">
          <i
            class="fa-solid fa-chevron-right"
            onClick={() => {
              increment();
            }}
          ></i>
        </a>
      </div>
    </div>
  );
};

export default Acceuil;
