import React, { useEffect, useState } from "react";

import NavBar from "../components/NavBar";
import axios from "axios";
import Cards from "../components/Cards";

const Acceuil = () => {
  const [inputValue, setInputValue] = useState("");
  let axio = "";
  const [data, setData] = useState([]);
  // const background = "https://image.tmdb.org/t/p/original" + data[0].backdrop_path;
  // console.log(background);
  const handleInputChange = (newInputValue) => {
    setInputValue(newInputValue);
  };

  console.log(data[0].backdrop_path);

  // _________________________________

  if (inputValue === "") {
    axio =
      "https://api.themoviedb.org/3/search/multi?api_key=864b6602f4018630491e67fa714381e6&query=av&page=1&language=fr-FR";
  } else {
    axio =
      "https://api.themoviedb.org/3/search/multi?api_key=864b6602f4018630491e67fa714381e6&query=" +
      inputValue +
      "&page=1&language=fr-FR";
  }
  useEffect(() => {
    axios.get(axio).then((res) => setData(res.data.results));
  }, [inputValue]);

  const slideRight = () => {
    document.querySelector(".line-1").style.transform = `translateX(-170vh)`;
  };
  const slideLeft = () => {
    document.querySelector(".line-1").style.transform = `translateX(138vh)`;
  };

  const slideRight2 = () => {
    document.querySelector(".line-2").style.transform = `translateX(-170vh)`;
  };
  const slideLeft2 = () => {
    document.querySelector(".line-2").style.transform = `translateX(138vh)`;
  };
  const slideRight3 = () => {
    document.querySelector(".line-3").style.transform = `translateX(-170vh)`;
  };
  const slideLeft3 = () => {
    document.querySelector(".line-3").style.transform = `translateX(138vh)`;
  };

  return (
    <div className="acceuil">
      <NavBar onInputChange={handleInputChange} />
      <div
        className="img-background"
        // style={{ background: }}
      ></div>
      <div className="card-container">
        <ul className="line-1">
          {data.slice(0, 12).map((movie) => (
            <Cards key={movie.id} movie={movie} />
          ))}
        </ul>
        <div className="btn">
          <i
            className="btn-droite fa-solid fa-chevron-left"
            onClick={slideRight}
          ></i>

          <i
            className="btn-gauche fa-solid fa-chevron-right"
            onClick={slideLeft}
          ></i>
        </div>

        <ul className="line-2">
          {data.slice(6, 18).map((movie) => (
            <Cards key={movie.id} movie={movie} />
          ))}
        </ul>
        <div className="btn2">
          <i
            className="btn-droite fa-solid fa-chevron-left"
            onClick={slideRight2}
          ></i>

          <i
            className="btn-gauche fa-solid fa-chevron-right"
            onClick={slideLeft2}
          ></i>
        </div>
        <ul className="line-3">
          {data.slice(8, 20).map((movie) => (
            <Cards key={movie.id} movie={movie} />
          ))}
        </ul>
        <div className="btn3">
          <i
            className="btn-droite fa-solid fa-chevron-left"
            onClick={slideRight3}
          ></i>

          <i
            className="btn-gauche fa-solid fa-chevron-right"
            onClick={slideLeft3}
          ></i>
        </div>
      </div>
    </div>
  );
};

export default Acceuil;
