import React, { useState } from "react";
import { NavLink } from "react-router-dom";

const NavBar = (props) => {
  const [inputValuee, setInputValuee] = useState("");
  const [showSearch, setShowSearch] = useState(true);
  const [showInputSearch, setShowInputSearch] = useState(false);

  const handleInput = () => {
    document.querySelector(".input-search").addEventListener("input", (e) => {
      setInputValuee(e.target.value);
    });

    props.onInputChange(inputValuee);
  };

  window.addEventListener("scroll", () => {
    if (window.scrollY > 20) {
      document.querySelector(".nav-bar").style.background = "black";
      document.querySelector(".nav-bar").style.opacity = "0.96";
    } else if (window.scrollY < 20) {
      document.querySelector(".nav-bar").style.background = `linear-gradient(
        180deg,
        rgba(0, 0, 0, 0.8085828081232493) 0%,
        rgba(255, 255, 255, 0) 100%`;
    }
  });

  return (
    <div className="nav-bar">
      <div className="burger-container">
        <svg
          xmlns="http://www.w3.org/2000/svg"
          x="0px"
          y="0px"
          viewBox="0,0,256,256"
          className="burger-menu"
        >
          <g
            fill="#ffffff"
            fill-rule="nonzero"
            stroke="none"
            stroke-width="1"
            stroke-linecap="butt"
            stroke-linejoin="miter"
            stroke-miterlimit="10"
            stroke-dasharray=""
            stroke-dashoffset="0"
            font-family="none"
            font-weight="none"
            font-size="none"
            text-anchor="none"
          >
            <g transform="scale(5.12,5.12)">
              <path d="M3,9c-0.36064,-0.0051 -0.69608,0.18438 -0.87789,0.49587c-0.18181,0.3115 -0.18181,0.69676 0,1.00825c0.18181,0.3115 0.51725,0.50097 0.87789,0.49587h44c0.36064,0.0051 0.69608,-0.18438 0.87789,-0.49587c0.18181,-0.3115 0.18181,-0.69676 0,-1.00825c-0.18181,-0.3115 -0.51725,-0.50097 -0.87789,-0.49587zM3,24c-0.36064,-0.0051 -0.69608,0.18438 -0.87789,0.49587c-0.18181,0.3115 -0.18181,0.69676 0,1.00825c0.18181,0.3115 0.51725,0.50097 0.87789,0.49587h44c0.36064,0.0051 0.69608,-0.18438 0.87789,-0.49587c0.18181,-0.3115 0.18181,-0.69676 0,-1.00825c-0.18181,-0.3115 -0.51725,-0.50097 -0.87789,-0.49587zM3,39c-0.36064,-0.0051 -0.69608,0.18438 -0.87789,0.49587c-0.18181,0.3115 -0.18181,0.69676 0,1.00825c0.18181,0.3115 0.51725,0.50097 0.87789,0.49587h44c0.36064,0.0051 0.69608,-0.18438 0.87789,-0.49587c0.18181,-0.3115 0.18181,-0.69676 0,-1.00825c-0.18181,-0.3115 -0.51725,-0.50097 -0.87789,-0.49587z"></path>
            </g>
          </g>
        </svg>
        <NavLink to="/">
          <li onClick={() => setShowSearch(true)}>Acceuil</li>
        </NavLink>
        <NavLink to="/films">
          <li onClick={() => setShowSearch(true)}>Films</li>
        </NavLink>
        <NavLink to="/series">
          <li onClick={() => setShowSearch(true)}>Séries</li>
        </NavLink>
        <NavLink to="/nouveautes">
          <li onClick={() => setShowSearch(false)}>Nouveautés films</li>
        </NavLink>
      </div>

      <img src="./img/logo.png" alt="" className="logo" />
      <div className="navigation">
        <NavLink to="/">
          <li onClick={() => setShowSearch(true)}>Acceuil</li>
        </NavLink>
        <NavLink to="/films">
          <li onClick={() => setShowSearch(true)}>Films</li>
        </NavLink>
        <NavLink to="/series">
          <li onClick={() => setShowSearch(true)}>Séries</li>
        </NavLink>
        <NavLink to="/nouveautes">
          <li onClick={() => setShowSearch(false)}>Nouveautés films</li>
        </NavLink>
      </div>
      <div className="right-nav">
        <div className="search">
          <img
            onClick={() => {
              setShowInputSearch(!showInputSearch);
            }}
            className={showSearch ? "loupe" : "loupe no-active"}
            alt="svgImg"
            src="data:image/svg+xml;base64,"
            // onClick={() => {
            //   document.querySelector(".input-search").style.width = "220px";
            //   document.querySelector(".search").style.background = "black";
            //   document.querySelector(".search").style.border =
            //     "1px solid white";
            // }}
          />

          <input
            className={
              showInputSearch ? "input-search activeInput" : "input-search"
            }
            type="search"
            onInput={handleInput}
          />
        </div>

        <div className="dropdown">
          <i className="fa-solid fa-user mainmenubtn"></i>
          <i className="fa-solid fa-caret-down arrow-down"></i>
          <div className="dropdown-child">
            <div className="profil">
              <img src="./img/logoProfil.jpeg" alt="" className="img-profil" />
              <p>Profil 1</p>
            </div>
            <div className="profil">
              <img src="./img/logoProfil2.jpeg" alt="" className="img-profil" />
              <p>Profil 2</p>
            </div>
            <div className="profil">
              <img src="./img/logoProfil3.jpeg" alt="" className="img-profil" />
              <p>Profil 3</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default NavBar;
