const Cards = ({ movie }) => {
  const genreFinder = () => {
    let genreArray = [];

    for (let i = 0; i < movie.genre_ids.length; i++) {
      switch (movie.genre_ids[i]) {
        case 28:
          genreArray.push("Action");
          break;
        case 12:
          genreArray.push("Aventure");
          break;
        case 16:
          genreArray.push("Animation");
          break;
        case 35:
          genreArray.push("Comédie");
          break;
        case 80:
          genreArray.push("Policier");
          break;
        case 99:
          genreArray.push("Documentaire");
          break;
        case 18:
          genreArray.push("Drame");
          break;
        case 10751:
          genreArray.push("Famille");
          break;
        case 14:
          genreArray.push("Fantasy");
          break;
        case 36:
          genreArray.push("Historique");
          break;
        case 27:
          genreArray.push("Horreur");
          break;
        case 10402:
          genreArray.push("Music");
          break;
        case 9648:
          genreArray.push("Mystère");
          break;
        case 10749:
          genreArray.push("Romance");
          break;
        case 878:
          genreArray.push("Science Fiction");
          break;
        case 10770:
          genreArray.push("TV Show");
          break;
        case 53:
          genreArray.push("Thriller");
          break;
        case 10752:
          genreArray.push("Guerre ");
          break;
        case 37:
          genreArray.push("Western");
          break;
        default:
          break;
      }
    }
    return genreArray.map((genre) => <li key={genre}>{genre}</li>);
  };

  const displayInfo = () => {
    document.querySelector(".info-hover").style.display = "flex";
  };

  return (
    <div className="cards">
      <div className="img-container">
        <img
          src={
            movie.poster_path
              ? "https://image.tmdb.org/t/p/original" + movie.poster_path
              : "./img/poster.jpg"
          }
          alt={movie.poster_path}
          className="img-card"
        />
      </div>
      <div className="info-hover">
        <h1 className="title">{movie.title ? movie.title : movie.name}</h1>
        <p className="note">{movie.vote_average.toFixed(1)}/10</p>
        <ul className="genre">
          {movie.genre_ids
            ? genreFinder()
            : movie.genres.map((genre) => <li key={genre}>{genre.name}</li>)}
        </ul>
        <p className="date">Date de parution : {movie.first_air_date}</p>
      </div>
    </div>
  );
};

export default Cards;
