import React from "react";
import { NavLink } from "react-router-dom";

const NavBar = () => {
  window.addEventListener("scroll", () => {
    if (window.scrollY > 20) {
      document.querySelector(".nav-bar").style.background = "black";
    } else if (window.scrollY < 20) {
      document.querySelector(".nav-bar").style.background = `linear-gradient(
        180deg,
        rgba(0, 0, 0, 0.8085828081232493) 0%,
        rgba(255, 255, 255, 0) 100%`;
    }
  });

  const rotate = () => {
    const arrow = document.querySelector(".arrow-down");

    arrow.style.transform = "rotate(180)";
  };

  return (
    <div className="nav-bar">
      <img src="./img/logo.png" alt="" className="logo" />
      <div className="navigation">
        <NavLink to="/">
          <li>Acceuil</li>
        </NavLink>
        <NavLink to="/films">
          <li>Films</li>
        </NavLink>
        <NavLink to="/series">
          <li>Séries</li>
        </NavLink>
        <NavLink to="/nouveautes">
          <li>Les plus regardés</li>
        </NavLink>
      </div>
      <div className="right-nav">
        <div className="search">
          <img
            className="loupe"
            alt="svgImg"
            src="data:image/svg+xml;base64,"
          />

          <input className="input-search" type="text" />
        </div>

        <div className="dropdown" onMouseOver={rotate}>
          <i className="fa-solid fa-user mainmenubtn"></i>
          <i className="fa-solid fa-caret-down arrow-down"></i>
          <div className="dropdown-child">
            <p>Profil 1</p>
            <p>Profil 2</p>
            <p>Profil 3</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default NavBar;
