import React, { useEffect, useState } from "react";

import NavBar from "../components/NavBar";
import axios from "axios";
import Cards from "../components/Cards";

const Acceuil = () => {
  const [inputValue, setInputValue] = useState("");
  let axio = "";
  const [data, setData] = useState([]);

  const handleInputChange = (newInputValue) => {
    setInputValue(newInputValue);
  };

  // _________________________________

  if (inputValue === "") {
    axio =
      "https://api.themoviedb.org/3/search/multi?api_key=864b6602f4018630491e67fa714381e6&query=av&page=1&language=fr-FR";
  } else {
    axio =
      "https://api.themoviedb.org/3/search/multi?api_key=864b6602f4018630491e67fa714381e6&query=" +
      inputValue +
      "&page=1&language=fr-FR";
  }
  useEffect(() => {
    axios.get(axio).then((res) => setData(res.data.results));
  }, [inputValue]);

  const slideRight = () => {
    document.querySelector(".line-2").style.transform = `translateX(-200vh)`;
  };

  return (
    <div className="acceuil">
      <NavBar onInputChange={handleInputChange} />
      <div className="img-background"></div>
      <div className="card-container">
        <ul className="line-1">
          {data.slice(0, 10).map((movie) => (
            <Cards key={movie.id} movie={movie} />
          ))}
        </ul>
        <div className="btn">
          <button className="btn-droite" onClick={slideRight}>
            Droite
          </button>
          <button>gauche</button>
        </div>

        <ul className="line-2">
          {data.slice(6, 11).map((movie) => (
            <Cards key={movie.id} movie={movie} />
          ))}
        </ul>
        <ul className="line-3">
          {data.slice(12, 17).map((movie) => (
            <Cards key={movie.id} movie={movie} />
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Acceuil;
